                    id: productConfig.id,
                    name: productConfig.name,
                    description: productConfig.description,
                });
                
                // Create price
                const price = await stripe.prices.create({
                    product: product.id,
                    unit_amount: productConfig.price,
                    currency: productConfig.currency,
                    recurring: {
