/**
 * StackPro API Server - Simplified Version for Testing
 * Express server with authentication endpoints only
 */

const express = require('express');
const cors = require('cors');
const helmet = require('helmet');
const rateLimit = require('express-rate-limit');
const { validateClientConfig } = require('../config/validation');
const { logger } = require('../utils/logger');

// Database and Auth
const bcrypt = require('bcrypt');
const jwt = require('jsonwebtoken');
const { v4: uuidv4 } = require('uuid');

// Services
const { DatabaseService } = require('../services/database-service');
const { StripeService } = require('../services/stripe-service');

// Stripe configuration  
const stripe = require('stripe')(process.env.STRIPE_SECRET_KEY);

const app = express();
const PORT = process.env.PORT || 3002;

// Middleware
app.use(helmet());
app.use(cors({
  origin: process.env.FRONTEND_URL || 'http://localhost:3000',
  credentials: true
}));

// Rate limiting
const limiter = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: 100, // limit each IP to 100 requests per windowMs
  message: 'Too many requests from this IP, please try again later.'
});
app.use('/api/', limiter);

// Body parsing
app.use(express.json({ limit: '10mb' }));
app.use(express.urlencoded({ extended: true }));

// Request logging
app.use((req, res, next) => {
  logger.info(`${req.method} ${req.path} - ${req.ip}`);
  next();
});

// Initialize services
const dbService = new DatabaseService();
const stripeService = new StripeService();

// Health check endpoint
app.get('/health', (req, res) => {
  res.json({ 
    status: 'healthy',
    timestamp: new Date().toISOString(),
    version: '1.0.0'
  });
});

// Authentication middleware
const authenticateToken = (req, res, next) => {
  const authHeader = req.headers['authorization'];
  const token = authHeader && authHeader.split(' ')[1];

  if (!token) {
    return res.status(401).json({ success: false, error: 'Access token required' });
  }

  jwt.verify(token, process.env.JWT_SECRET, (err, user) => {
    if (err) {
      return res.status(403).json({ success: false, error: 'Invalid token' });
    }
    req.user = user;
    next();
  });
};

// User registration endpoint
app.post('/api/auth/signup', async (req, res) => {
  try {
    const { firstName, lastName, email, company, password, plan } = req.body;

    // Validate input
    if (!firstName || !lastName || !email || !password || !company || !plan) {
      return res.status(400).json({
        success: false,
        error: 'All fields are required'
      });
    }

    // Check if user already exists
    const existingUser = await dbService.getUserByEmail(email);
    if (existingUser) {
      return res.status(409).json({
        success: false,
        error: 'User already exists with this email'
      });
    }

    // Hash password
    const hashedPassword = await bcrypt.hash(password, 10);

    // Generate client ID
    const clientId = `${company.toLowerCase().replace(/[^a-z0-9]/g, '')}-${uuidv4().slice(0, 8)}`;

    // Create user record
    const user = {
      id: uuidv4(),
      firstName,
      lastName,
      email,
      company,
      password: hashedPassword,
      plan,
      clientId,
      status: 'trial',
      createdAt: new Date().toISOString()
    };

    // Save user to database
    await dbService.createUser(user);

    // Generate JWT token
    const token = jwt.sign(
      { 
        userId: user.id, 
        email: user.email, 
        clientId: user.clientId 
      },
      process.env.JWT_SECRET,
      { expiresIn: '24h' }
    );

    // Log successful signup
    logger.info('‚úÖ New user signed up:', { 
      clientId: user.clientId,
      email: user.email,
      plan: user.plan
    });

    res.status(201).json({
      success: true,
      message: 'Account created successfully - Infrastructure provisioning started!',
      user: {
        id: user.id,
        firstName: user.firstName,
        lastName: user.lastName,
        email: user.email,
        company: user.company,
        clientId: user.clientId,
        plan: user.plan,
        status: user.status
      },
      token,
      dashboardUrl: `/dashboard?client=${user.clientId}`
    });

  } catch (error) {
    logger.error('‚ùå Signup error:', error);
    res.status(500).json({
      success: false,
      error: 'Internal server error'
    });
  }
});

// User login endpoint
