              { icon: 'üíé', title: 'Premium Quality', desc: 'Glassmorphism effects create depth and sophistication that commands respect', color: 'accent' },
              { icon: 'üîí', title: 'Enterprise Trust', desc: 'Professional color palette builds confidence and reliability with business clients', color: 'success' },
              { icon: '‚ö°', title: 'Performance Focus', desc: 'Optimized animations and interactions that feel instant and responsive', color: 'warning' },
              { icon: 'üé®', title: 'Visual Excellence', desc: 'Every detail crafted to create a cohesive, premium user experience', color: 'info' },
              { icon: 'üåü', title: 'Competitive Edge', desc: 'Stand out from generic solutions with distinctive, memorable branding', color: 'primary' }
            ].map((feature, index) => (
              <motion.div 
                key={feature.title}
                className="bg-surface rounded-2xl p-6 text-text-light border border-border hover:border-primary/40 transition-all duration-300"
                style={{ backdropFilter: 'blur(10px)' }}
                initial={{ opacity: 0, y: 30 }}
