@tailwind base;
@tailwind components;
@tailwind utilities;

/* Premium StackPro Color System - CSS Variables */
:root {
  /* Primary Colors */
  --color-primary: #6366F1;        /* Electric Indigo */
  --color-primary-hover: #4F46E5;  /* Darker Electric Indigo */
  --color-accent: #22D3EE;         /* Aqua Sky */
  --color-background: #0F172A;     /* Charcoal Slate */
  --color-surface: #1E293B;       /* Slate Gray */
  --color-surface-light: #334155;  /* Lighter surface */
  --color-text-light: #F1F5F9;    /* White Smoke */
  --color-text-dark: #0F172A;     /* Dark text */
  --color-border: #334155;        /* Border color */
  
  /* Extended Palette */
  --color-success: #10B981;       /* Emerald Green */
  --color-warning: #F59E0B;       /* Amber Gold */
  --color-error: #EF4444;         /* Rose Red */
  --color-info: #38BDF8;          /* Sky Blue */
  
  /* Glassmorphism */
  --glass-primary: rgba(99, 102, 241, 0.1);
  --glass-accent: rgba(34, 211, 238, 0.1);
  --glass-surface: rgba(255, 255, 255, 0.05);
}

/* Dark theme base styling */
html,
body {
  padding: 0;
  margin: 0;
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Oxygen,
    Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue, sans-serif;
  background-color: var(--color-background);
  color: var(--color-text-light);
  scroll-behavior: smooth;
}

a {
  color: inherit;
  text-decoration: none;
}

* {
  box-sizing: border-box;
}

/* Premium Typography */
h1, h2, h3, h4, h5, h6 {
  font-weight: 600;
  line-height: 1.2;
  color: var(--color-text-light);
}

/* Premium Animations */
.animate-fade-in {
  animation: fadeIn 0.6s ease-in-out;
}

.animate-slide-up {
  animation: slideUp 0.6s ease-out;
}

.animate-bounce-in {
  animation: bounceIn 0.8s ease-out;
}

.animate-pulse-glow {
  animation: pulseGlow 2s ease-in-out infinite;
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes slideUp {
  from {
    transform: translateY(20px);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
}

@keyframes bounceIn {
  0% {
    transform: scale(0.3);
    opacity: 0;
  }
  50% { transform: scale(1.05); }
  70% { transform: scale(0.9); }
  100% {
    transform: scale(1);
    opacity: 1;
  }
}

@keyframes pulseGlow {
  0%, 100% {
    box-shadow: 0 0 0 0 rgba(99, 102, 241, 0.7);
  }
  50% {
    box-shadow: 0 0 0 10px rgba(99, 102, 241, 0);
  }
}

/* Premium Gradient Utilities */
.text-gradient-primary {
  background: linear-gradient(135deg, var(--color-primary) 0%, var(--color-accent) 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.bg-gradient-primary {
  background: linear-gradient(135deg, var(--color-primary) 0%, var(--color-primary-hover) 100%);
}

.bg-gradient-accent {
  background: linear-gradient(135deg, var(--color-primary) 0%, var(--color-accent) 100%);
}

.bg-gradient-dark {
  background: linear-gradient(135deg, var(--color-background) 0%, var(--color-surface) 50%, var(--color-surface-light) 100%);
}

/* Glassmorphism Utilities */
.glass-primary {
  background: var(--glass-primary);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.glass-accent {
  background: var(--glass-accent);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.glass-surface {
  background: var(--glass-surface);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.05);
}

.glass-card {
  background: linear-gradient(135deg, var(--glass-surface) 0%, rgba(255, 255, 255, 0.02) 100%);
  backdrop-filter: blur(15px);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

/* Premium Shadows */
.shadow-premium {
  box-shadow: 0 8px 32px rgba(99, 102, 241, 0.3);
}

.shadow-accent {
  box-shadow: 0 8px 32px rgba(34, 211, 238, 0.2);
}

.shadow-dark {
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.3);
}

.shadow-glow {
  box-shadow: 0 0 40px rgba(99, 102, 241, 0.4);
}

/* Premium Form Controls */
.form-input {
  @apply w-full px-4 py-3 rounded-xl border border-border bg-surface text-text-light placeholder-text-light/60;
  @apply focus:ring-2 focus:ring-primary focus:border-transparent outline-none transition-all duration-200;
