  yellow: '\x1b[33m',
  blue: '\x1b[34m',
  reset: '\x1b[0m',
  bold: '\x1b[1m'
};

function log(message, color = 'reset') {
  console.log(`${colors[color]}${message}${colors.reset}`);
}

// Configuration with adjusted limits
const CONFIG = {
  profile: 'Stackbox',
  region: 'us-west-2',
  accountId: '304052673868',
  tags: {
    Project: 'StackPro',
    Env: 'FreeTier',
    Purpose: 'AbuseProtection',
