#!/usr/bin/env node

/**
 * StackPro Production Deployment to AWS Amplify
 * Deploys frontend to stackpro.io with SSL certificate
 */

const AWS = require('aws-sdk');
const fs = require('fs');
const path = require('path');

// Production Configuration
const PRODUCTION_CONFIG = {
  region: 'us-west-2',
  appName: 'StackPro-Production',
  repository: 'https://github.com/frotofraggins/StackBox.git',
  branch: 'main',
  domain: 'stackpro.io',
  profile: 'Stackbox',
  accountId: '304052673868',
  tags: {
    Project: 'StackPro',
    Environment: 'Production',
    Owner: 'Ops',
    Purpose: 'Customer-Production',
    CostCenter: 'Revenue'
  },
  environmentVariables: {
    // Production environment configuration
    NEXT_PUBLIC_ENV: 'production',
    NEXT_PUBLIC_API_URL: 'https://api.stackpro.io',
    NEXT_PUBLIC_BACKEND_URL: 'https://api.stackpro.io',
    NEXT_PUBLIC_WEBSOCKET_URL: 'wss://ws.stackpro.io',
    NEXT_PUBLIC_FREE_TIER: 'false',
    AI_ENABLED: 'true',
    NEXT_PUBLIC_AI_ENABLED: 'true',
    NEXT_PUBLIC_MESSAGING_ENABLED: 'true',
    NEXT_PUBLIC_SITE_BUILDER_ENABLED: 'true',
    NEXT_PUBLIC_STRIPE_PUBLISHABLE_KEY: process.env.STRIPE_PUBLISHABLE_KEY || 'pk_live_production',
    NODE_ENV: 'production',
    NEXT_TELEMETRY_DISABLED: '1',
    // Production features - no limits
    NEXT_PUBLIC_MAX_USERS: '1000',
    NEXT_PUBLIC_MAX_STORAGE_GB: '100',
    NEXT_PUBLIC_MAX_EMAILS_PER_MONTH: '10000',
    NEXT_PUBLIC_FEATURES_CRM: 'full',
    NEXT_PUBLIC_FEATURES_FILES: 'full',
    NEXT_PUBLIC_FEATURES_WEBSITE: 'full',
    NEXT_PUBLIC_FEATURES_AI: 'full'
  }
};

const colors = {
  green: '\x1b[32m',
  red: '\x1b[31m',
  yellow: '\x1b[33m',
  blue: '\x1b[34m',
  reset: '\x1b[0m',
  bold: '\x1b[1m'
};

function log(message, color = 'reset') {
  console.log(`${colors[color]}${message}${colors.reset}`);
}

class ProductionAmplifyDeployer {
  constructor() {
    // Use Stackbox profile for production
    this.amplify = new AWS.Amplify({ 
      region: PRODUCTION_CONFIG.region,
      credentials: new AWS.SharedIniFileCredentials({ profile: PRODUCTION_CONFIG.profile })
    });
    this.route53 = new AWS.Route53({ 
      region: PRODUCTION_CONFIG.region,
      credentials: new AWS.SharedIniFileCredentials({ profile: PRODUCTION_CONFIG.profile })
    });
    this.acm = new AWS.ACM({ 
      region: 'us-east-1', // ACM for CloudFront must be in us-east-1
      credentials: new AWS.SharedIniFileCredentials({ profile: PRODUCTION_CONFIG.profile })
    });
    
    this.appId = null;
    this.certificateArn = null;
    this.hostedZoneId = null;
  }

  async init() {
    log('üöÄ Starting StackPro Production Deployment', 'bold');
    log(`üìç Region: ${PRODUCTION_CONFIG.region}`, 'blue');
    log(`üåê Domain: ${PRODUCTION_CONFIG.domain}`, 'blue');
    log(`üì¶ Repository: ${PRODUCTION_CONFIG.repository}`, 'blue');
    log(`üë§ Profile: ${PRODUCTION_CONFIG.profile} (${PRODUCTION_CONFIG.accountId})`, 'blue');
    
    try {
      await this.validatePrerequisites();
      await this.findSSLCertificate();
      await this.createAmplifyApp();
      await this.configureBuildSettings();
      await this.setEnvironmentVariables();
      await this.createBranch();
      await this.startBuild();
      await this.setupCustomDomain();
      await this.verifyDeployment();
      
      log('‚úÖ Production deployment completed successfully!', 'green');
      log('üéâ StackPro is now live at https://stackpro.io', 'green');
      
    } catch (error) {
      log(`‚ùå Deployment failed: ${error.message}`, 'red');
      console.log('üîÑ Running rollback...');
      await this.rollback();
      process.exit(1);
    }
  }

  async validatePrerequisites() {
    log('üîç Validating deployment prerequisites...', 'bold');
    
    try {
      // Verify we're in the correct AWS account
      const sts = new AWS.STS({
        credentials: new AWS.SharedIniFileCredentials({ profile: PRODUCTION_CONFIG.profile })
      });
      const identity = await sts.getCallerIdentity().promise();
      
      if (identity.Account !== PRODUCTION_CONFIG.accountId) {
        throw new Error(`Wrong AWS account: ${identity.Account} (expected ${PRODUCTION_CONFIG.accountId})`);
      }
      
      log(`‚úÖ AWS Account: ${identity.Account}`, 'green');
      
      // Verify domain is in this account
      const route53Domains = new AWS.Route53Domains({
        region: 'us-east-1',
        credentials: new AWS.SharedIniFileCredentials({ profile: PRODUCTION_CONFIG.profile })
      });
      
      const domains = await route53Domains.listDomains().promise();
      const domain = domains.Domains.find(d => d.DomainName === PRODUCTION_CONFIG.domain);
      
      if (!domain) {
        throw new Error(`Domain ${PRODUCTION_CONFIG.domain} not found in account ${PRODUCTION_CONFIG.accountId}`);
      }
      
      log(`‚úÖ Domain: ${domain.DomainName} (expires ${domain.Expiry})`, 'green');
      
      // Find hosted zone
      const hostedZones = await this.route53.listHostedZones().promise();
      const hostedZone = hostedZones.HostedZones.find(z => z.Name === `${PRODUCTION_CONFIG.domain}.`);
      
      if (!hostedZone) {
        throw new Error(`Hosted zone for ${PRODUCTION_CONFIG.domain} not found`);
      }
      
      this.hostedZoneId = hostedZone.Id.split('/')[2];
      log(`‚úÖ Hosted Zone: ${this.hostedZoneId}`, 'green');
      
    } catch (error) {
      throw new Error(`Prerequisite validation failed: ${error.message}`);
    }
  }

  async findSSLCertificate() {
    log('üîí Looking for SSL certificate...', 'bold');
    
    try {
      const certificates = await this.acm.listCertificates({
        CertificateStatuses: ['ISSUED']
      }).promise();
      
      // Find certificate for our domain
      const cert = certificates.CertificateSummaryList.find(c => 
        c.DomainName === PRODUCTION_CONFIG.domain || 
        c.SubjectAlternativeNameSummary?.includes(PRODUCTION_CONFIG.domain)
      );
      
      if (!cert) {
        log('‚ùå No issued SSL certificate found', 'red');
        log('üîß Please run the SSL certificate setup first:', 'yellow');
        log('   node scripts/request-production-ssl.js', 'blue');
        throw new Error('SSL certificate not found');
      }
      
      // Verify certificate is issued
      const certDetails = await this.acm.describeCertificate({
        CertificateArn: cert.CertificateArn
      }).promise();
      
      if (certDetails.Certificate.Status !== 'ISSUED') {
        throw new Error(`Certificate status is ${certDetails.Certificate.Status}, expected ISSUED`);
      }
      
      this.certificateArn = cert.CertificateArn;
      log(`‚úÖ SSL Certificate: ${cert.CertificateArn}`, 'green');
      log(`   Status: ${certDetails.Certificate.Status}`, 'blue');
      log(`   Domains: ${certDetails.Certificate.DomainName}`, 'blue');
      
    } catch (error) {
      throw new Error(`SSL certificate validation failed: ${error.message}`);
    }
  }

  async createAmplifyApp() {
    log('üì± Creating/updating Amplify app...', 'bold');
    
    try {
      // Check if app already exists
      const existingApps = await this.amplify.listApps().promise();
      const existingApp = existingApps.apps.find(app => app.name === PRODUCTION_CONFIG.appName);
      
      if (existingApp) {
        log(`üì± Using existing app: ${existingApp.name} (${existingApp.appId})`, 'green');
        this.appId = existingApp.appId;
        
        // Update app configuration
        await this.amplify.updateApp({
          appId: this.appId,
          name: PRODUCTION_CONFIG.appName,
          description: 'StackPro Production - Customer-Facing SaaS Platform',
          repository: PRODUCTION_CONFIG.repository,
          environmentVariables: PRODUCTION_CONFIG.environmentVariables,
          buildSpec: this.getBuildSpec(),
          tags: PRODUCTION_CONFIG.tags
        }).promise();
        
        log(`‚úÖ Updated existing app configuration`, 'green');
        return;
      }

      // Create new app
      const params = {
        name: PRODUCTION_CONFIG.appName,
        repository: PRODUCTION_CONFIG.repository,
        platform: 'WEB',
        description: 'StackPro Production - Complete Business Platform SaaS',
        tags: PRODUCTION_CONFIG.tags,
        environmentVariables: PRODUCTION_CONFIG.environmentVariables,
        buildSpec: this.getBuildSpec(),
        enableBranchAutoBuild: true,
        enableBranchAutoDeploy: true,
        enablePullRequestPreview: false
      };

      const result = await this.amplify.createApp(params).promise();
      this.appId = result.app.appId;
      
      log(`‚úÖ Created new Amplify app: ${result.app.name} (${this.appId})`, 'green');
      
    } catch (error) {
      throw new Error(`Failed to create/update Amplify app: ${error.message}`);
    }
  }

  getBuildSpec() {
    return `version: 1
applications:
  - frontend:
      phases:
        preBuild:
          commands:
            - echo "Installing dependencies for production deployment"
            - cd frontend
            - npm ci --production
        build:
          commands:
            - echo "Building Next.js app for production"
            - echo "Environment: $NEXT_PUBLIC_ENV"
            - echo "AI enabled: $AI_ENABLED"
            - echo "API URL: $NEXT_PUBLIC_API_URL"
            - npm run build
        postBuild:
          commands:
            - echo "Production build completed"
            - echo "Build artifacts ready for deployment"
      artifacts:
        baseDirectory: frontend/.next
        files:
          - '**/*'
      cache:
        paths:
          - frontend/node_modules/**/*
    appRoot: ./`;
  }

  async configureBuildSettings() {
    log('‚öôÔ∏è Configuring build settings...', 'bold');
    
    try {
      const params = {
        appId: this.appId,
        buildSpec: this.getBuildSpec()
      };

      await this.amplify.updateApp(params).promise();
      log('‚úÖ Build settings configured', 'green');
      
    } catch (error) {
      throw new Error(`Build settings configuration failed: ${error.message}`);
    }
  }

  async setEnvironmentVariables() {
    log('üîß Setting production environment variables...', 'bold');
    
    try {
      // Update app-level environment variables
      await this.amplify.updateApp({
        appId: this.appId,
        environmentVariables: PRODUCTION_CONFIG.environmentVariables
      }).promise();
      
      log('‚úÖ Environment variables configured', 'green');
      log(`   API URL: ${PRODUCTION_CONFIG.environmentVariables.NEXT_PUBLIC_API_URL}`, 'blue');
      log(`   WebSocket: ${PRODUCTION_CONFIG.environmentVariables.NEXT_PUBLIC_WEBSOCKET_URL}`, 'blue');
      log(`   AI Enabled: ${PRODUCTION_CONFIG.environmentVariables.AI_ENABLED}`, 'blue');
      
    } catch (error) {
      throw new Error(`Environment variables configuration failed: ${error.message}`);
    }
  }

  async createBranch() {
    log('üåø Creating/updating main branch...', 'bold');
    
    try {
      // Check if branch exists
      try {
        const existingBranch = await this.amplify.getBranch({
          appId: this.appId,
          branchName: PRODUCTION_CONFIG.branch
        }).promise();
        
        // Update existing branch
        await this.amplify.updateBranch({
          appId: this.appId,
          branchName: PRODUCTION_CONFIG.branch,
          description: 'Production main branch',
          enableAutoBuild: true,
          environmentVariables: PRODUCTION_CONFIG.environmentVariables,
          tags: PRODUCTION_CONFIG.tags
        }).promise();
        
        log(`‚úÖ Updated existing branch: ${PRODUCTION_CONFIG.branch}`, 'green');
        
      } catch (branchError) {
        if (branchError.code === 'NotFoundException') {
          // Create new branch
          const params = {
            appId: this.appId,
            branchName: PRODUCTION_CONFIG.branch,
            description: 'Production main branch for customer access',
            enableAutoBuild: true,
            enablePullRequestPreview: false,
            environmentVariables: PRODUCTION_CONFIG.environmentVariables,
            tags: PRODUCTION_CONFIG.tags
          };

          await this.amplify.createBranch(params).promise();
          log(`‚úÖ Created new branch: ${PRODUCTION_CONFIG.branch}`, 'green');
        } else {
          throw branchError;
        }
      }
      
    } catch (error) {
      throw new Error(`Branch creation/update failed: ${error.message}`);
    }
  }

  async startBuild() {
    log('üî® Starting production build...', 'bold');
    
    try {
      const params = {
        appId: this.appId,
        branchName: PRODUCTION_CONFIG.branch,
        jobType: 'RELEASE'
      };

      const result = await this.amplify.startJob(params).promise();
      const jobId = result.jobSummary.jobId;
      
      log(`‚úÖ Build started: ${jobId}`, 'green');
      log('‚è≥ Waiting for build to complete...', 'yellow');

      // Poll for build completion with longer timeout for production builds
      let attempts = 0;
      const maxAttempts = 80; // 40 minutes max for production builds
      
      while (attempts < maxAttempts) {
        await new Promise(resolve => setTimeout(resolve, 30000)); // Wait 30 seconds
        
        const statusParams = {
          appId: this.appId,
          branchName: PRODUCTION_CONFIG.branch,
          jobId: jobId
        };
        
        const status = await this.amplify.getJob(statusParams).promise();
        log(`üîÑ Build status: ${status.job.summary.status} (${Math.floor(attempts/2)} minutes)`, 'blue');
        
        if (status.job.summary.status === 'SUCCEED') {
          log('‚úÖ Production build completed successfully!', 'green');
          return;
        } else if (status.job.summary.status === 'FAILED') {
          // Get build logs for debugging
          log('‚ùå Build failed. Recent log entries:', 'red');
          if (status.job.steps) {
            status.job.steps.forEach(step => {
              if (step.logUrl) {
                log(`   Step: ${step.stepName}`, 'yellow');
                log(`   Log: ${step.logUrl}`, 'blue');
              }
            });
          }
          throw new Error('Production build failed. Check build logs in Amplify console.');
        }
        
        attempts++;
      }
      
      throw new Error('Build timed out after 40 minutes');
      
    } catch (error) {
      throw new Error(`Build failed: ${error.message}`);
    }
  }

  async setupCustomDomain() {
    log('üåê Setting up custom domain with SSL...', 'bold');
    
    try {
      // Check if domain association already exists
      const existingDomains = await this.amplify.listDomainAssociations({
        appId: this.appId
      }).promise();
      
      const existingDomain = existingDomains.domainAssociations.find(
        domain => domain.domainName === PRODUCTION_CONFIG.domain
      );
      
      if (existingDomain) {
        log(`‚úÖ Using existing domain association: ${existingDomain.domainName}`, 'green');
        log(`   Status: ${existingDomain.domainStatus}`, 'blue');
        return;
      }

      // Create new domain association with SSL certificate
      const params = {
        appId: this.appId,
        domainName: PRODUCTION_CONFIG.domain,
        enableAutoSubDomain: false,
        subDomainSettings: [
          {
            prefix: '',
            branchName: PRODUCTION_CONFIG.branch
          },
          {
            prefix: 'www',
            branchName: PRODUCTION_CONFIG.branch
          }
        ],
        certificateSettings: {
          type: 'AMPLIFY_MANAGED',
          certificateVerificationDNSRecord: undefined // Let Amplify manage
        }
      };

      const result = await this.amplify.createDomainAssociation(params).promise();
      
      log(`‚úÖ Domain association created: ${PRODUCTION_CONFIG.domain}`, 'green');
      log(`   Status: ${result.domainAssociation.domainStatus}`, 'blue');
      log(`   Certificate: Amplify-managed SSL`, 'blue');
      
      // Wait for domain verification (this can take a few minutes)
      log('‚è≥ Waiting for domain verification...', 'yellow');
      
      let verificationAttempts = 0;
      const maxVerificationAttempts = 20; // 10 minutes
      
      while (verificationAttempts < maxVerificationAttempts) {
        await new Promise(resolve => setTimeout(resolve, 30000)); // Wait 30 seconds
        
        const domainStatus = await this.amplify.getDomainAssociation({
          appId: this.appId,
          domainName: PRODUCTION_CONFIG.domain
        }).promise();
        
        log(`üîÑ Domain status: ${domainStatus.domainAssociation.domainStatus}`, 'blue');
        
        if (domainStatus.domainAssociation.domainStatus === 'AVAILABLE') {
          log('‚úÖ Domain verification completed!', 'green');
          break;
        } else if (domainStatus.domainAssociation.domainStatus === 'FAILED') {
          throw new Error('Domain verification failed');
        }
        
        verificationAttempts++;
      }
      
      if (verificationAttempts >= maxVerificationAttempts) {
        log('‚ö†Ô∏è Domain verification taking longer than expected', 'yellow');
        log('   The domain will continue verifying in the background', 'blue');
      }
      
    } catch (error) {
      throw new Error(`Custom domain setup failed: ${error.message}`);
    }
  }

  async verifyDeployment() {
    log('‚úÖ Verifying production deployment...', 'bold');
    
    try {
      // Get app details
      const app = await this.amplify.getApp({ appId: this.appId }).promise();
      const branch = await this.amplify.getBranch({
        appId: this.appId,
        branchName: PRODUCTION_CONFIG.branch
      }).promise();

      // Get domain association details
      let customDomainUrl = `https://${PRODUCTION_CONFIG.domain}`;
      try {
        const domainAssoc = await this.amplify.getDomainAssociation({
          appId: this.appId,
          domainName: PRODUCTION_CONFIG.domain
        }).promise();
        customDomainUrl = `https://${domainAssoc.domainAssociation.domainName}`;
      } catch (domainError) {
        log('‚ö†Ô∏è Custom domain not configured yet', 'yellow');
      }

      log('\nüéâ Production Deployment Summary:', 'bold');
      log(`üì± App Name: ${app.app.name}`, 'green');
      log(`üÜî App ID: ${this.appId}`, 'blue');
      log(`üåø Branch: ${branch.branch.branchName}`, 'blue');
      log(`üåê Default URL: https://${branch.branch.displayName}.${app.app.defaultDomain}`, 'blue');
      log(`üéØ Custom Domain: ${customDomainUrl}`, 'green');
      log(`üìç Region: ${PRODUCTION_CONFIG.region}`, 'blue');
      log(`üè∑Ô∏è Environment: Production`, 'blue');
      log(`üîí SSL: Enabled`, 'green');
      
      log('\nüîó Important Links:', 'bold');
      log(`üìä Amplify Console: https://console.aws.amazon.com/amplify/home?region=${PRODUCTION_CONFIG.region}#/${this.appId}`, 'blue');
      log(`üåê Live Site: ${customDomainUrl}`, 'green');
      log(`üìà CloudWatch Logs: https://console.aws.amazon.com/cloudwatch/home?region=${PRODUCTION_CONFIG.region}`, 'blue');
      
      // Save deployment summary
      const summary = {
        appId: this.appId,
        appName: app.app.name,
        defaultDomain: `${branch.branch.displayName}.${app.app.defaultDomain}`,
        customDomain: PRODUCTION_CONFIG.domain,
        customDomainUrl: customDomainUrl,
        region: PRODUCTION_CONFIG.region,
        repository: PRODUCTION_CONFIG.repository,
        branch: PRODUCTION_CONFIG.branch,
        certificateArn: this.certificateArn,
        hostedZoneId: this.hostedZoneId,
        deployedAt: new Date().toISOString(),
        environment: 'production'
      };
      
      const summaryPath = path.join(__dirname, '..', 'logs', 'production-deployment-summary.json');
      fs.writeFileSync(summaryPath, JSON.stringify(summary, null, 2));
      
      log(`üìÑ Deployment summary saved: ${summaryPath}`, 'blue');
      
      return summary;
      
    } catch (error) {
      throw new Error(`Deployment verification failed: ${error.message}`);
    }
  }

  async rollback() {
    log('üîÑ Starting rollback process...', 'yellow');
    
    try {
      if (this.appId) {
        log('‚ö†Ô∏è Production rollback requires manual review', 'yellow');
        log('üìù Consider these actions:', 'blue');
        log('1. Check build logs in Amplify console', 'blue');
        log('2. Verify environment variables are correct', 'blue');
        log('3. Ensure SSL certificate is valid', 'blue');
        log('4. Check domain DNS settings', 'blue');
        log('\n‚ùå Automatic rollback disabled for production safety', 'red');
      }
      
    } catch (error) {
      log(`‚ùå Rollback information failed: ${error.message}`, 'red');
    }
  }
}

// Health check after deployment
async function runProductionHealthCheck() {
  log('üè• Running production health check...', 'bold');
  
  try {
    // Test main domain
    const https = require('https');
    const url = `https://${PRODUCTION_CONFIG.domain}`;
    
    await new Promise((resolve, reject) => {
      const req = https.get(url, (res) => {
        if (res.statusCode === 200) {
          log(`‚úÖ ${url} is responding (${res.statusCode})`, 'green');
          resolve();
        } else {
          reject(new Error(`HTTP ${res.statusCode}`));
        }
      });
      
      req.on('error', reject);
      req.setTimeout(10000, () => reject(new Error('Timeout')));
    });
    
    // Test www redirect
    const wwwUrl = `https://www.${PRODUCTION_CONFIG.domain}`;
    await new Promise((resolve, reject) => {
      const req = https.get(wwwUrl, (res) => {
        if (res.statusCode === 301 || res.statusCode === 302 || res.statusCode === 200) {
          log(`‚úÖ ${wwwUrl} is responding (${res.statusCode})`, 'green');
          resolve();
        } else {
          reject(new Error(`HTTP ${res.statusCode}`));
        }
      });
      
      req.on('error', reject);
      req.setTimeout(10000, () => reject(new Error('Timeout')));
    });
    
    log('üéâ Production health check passed!', 'green');
    
  } catch (error) {
    log(`‚ö†Ô∏è Health check issue: ${error.message}`, 'yellow');
    log('   This may be temporary during initial deployment', 'blue');
  }
}

// Main execution
async function main() {
  const args = process.argv.slice(2);
  
  if (args.includes('--help')) {
    console.log('üìñ StackPro Production Amplify Deployment');
    console.log('');
    console.log('Usage:');
    console.log('  node scripts/deploy-amplify-production.js        # Deploy to production');
    console.log('  node scripts/deploy-amplify-production.js --test # Run health check only');
    console.log('  node scripts/deploy-amplify-production.js --help # Show this help');
    return;
  }
  
  if (args.includes('--test')) {
    await runProductionHealthCheck();
    return;
  }
  
  // Main deployment
  const deployer = new ProductionAmplifyDeployer();
  await deployer.init();
  
  // Run health check after deployment
  log('\nüè• Running post-deployment health check...', 'blue');
  await new Promise(resolve => setTimeout(resolve, 30000)); // Wait 30 seconds for deployment to propagate
  await runProductionHealthCheck();
}

// Execute if run directly
if (require.main === module) {
  main().catch(error => {
    console.error('‚ùå Production deployment failed:', error.message);
    process.exit(1);
  });
}

module.exports = { ProductionAmplifyDeployer, runProductionHealthCheck };

#!/usr/bin/env node

/**
 * AWS Amplify Sandbox Deployment Script
 * Deploys StackPro frontend to sandbox.stackpro.io in free-tier mode
 */

const AWS = require('aws-sdk');
const fs = require('fs');
const path = require('path');

// Configuration
const CONFIG = {
  region: 'us-west-2',
  appName: 'StackPro-Sandbox',
  repository: 'https://github.com/frotofraggins/StackBox.git',
  branch: 'main',
  domain: 'sandbox.stackpro.io',
  tags: {
    Project: 'StackPro',
    Environment: 'FreeTier',
    Owner: 'Ops',
    Purpose: 'Sandbox',
    CostCenter: 'FreeTier'
  },
  environmentVariables: {
    // Free-tier sandbox configuration
    NEXT_PUBLIC_ENV: 'sandbox',
    NEXT_PUBLIC_API_URL: 'https://api-sandbox.stackpro.io',
    NEXT_PUBLIC_BACKEND_URL: 'https://api-sandbox.stackpro.io',
    NEXT_PUBLIC_WEBSOCKET_URL: 'wss://api-sandbox.stackpro.io',
    NEXT_PUBLIC_FREE_TIER: 'true',
    AI_ENABLED: 'false',
    NEXT_PUBLIC_AI_ENABLED: 'false',
    NEXT_PUBLIC_MESSAGING_ENABLED: 'true',
    NEXT_PUBLIC_SITE_BUILDER_ENABLED: 'true',
    NEXT_PUBLIC_STRIPE_PUBLISHABLE_KEY: 'pk_test_sandbox', // Sandbox Stripe key
    NODE_ENV: 'production',
    NEXT_TELEMETRY_DISABLED: '1',
    // Limit features for free tier
    NEXT_PUBLIC_MAX_USERS: '10',
    NEXT_PUBLIC_MAX_STORAGE_MB: '100',
    NEXT_PUBLIC_MAX_EMAILS_PER_MONTH: '100',
    NEXT_PUBLIC_FEATURES_CRM: 'basic',
    NEXT_PUBLIC_FEATURES_FILES: 'basic',
    NEXT_PUBLIC_FEATURES_WEBSITE: 'basic'
  }
};

class AmplifyDeployer {
  constructor() {
    this.amplify = new AWS.Amplify({ region: CONFIG.region });
    this.route53 = new AWS.Route53({ region: CONFIG.region });
    this.acm = new AWS.ACM({ region: 'us-east-1' }); // ACM certs must be in us-east-1 for CloudFront
    this.appId = null;
    this.domainAssociationId = null;
  }

  async init() {
    console.log('üöÄ Starting StackPro Sandbox Deployment to AWS Amplify...');
    console.log(`üìç Region: ${CONFIG.region}`);
    console.log(`üåê Domain: ${CONFIG.domain}`);
    console.log(`üì¶ Repository: ${CONFIG.repository}`);
    
    try {
      await this.createAmplifyApp();
      await this.configureBuildSettings();
      await this.setEnvironmentVariables();
      await this.createBranch();
      await this.startBuild();
      await this.setupDomain();
      await this.configureSSL();
      await this.verifyDeployment();
      console.log('‚úÖ Deployment completed successfully!');
    } catch (error) {
      console.error('‚ùå Deployment failed:', error.message);
      console.log('üîÑ Running rollback...');
      await this.rollback();
      process.exit(1);
    }
  }

  async createAmplifyApp() {
    console.log('üì± Creating Amplify app...');
    
    try {
      // Check if app already exists
      const existingApps = await this.amplify.listApps().promise();
      const existingApp = existingApps.apps.find(app => app.name === CONFIG.appName);
      
      if (existingApp) {
        console.log(`üì± Using existing app: ${existingApp.name} (${existingApp.appId})`);
        this.appId = existingApp.appId;
        return;
      }

      const params = {
        name: CONFIG.appName,
        repository: CONFIG.repository,
        platform: 'WEB',
        description: 'StackPro Sandbox Frontend - Free Tier Demo Environment',
        tags: CONFIG.tags,
        environmentVariables: CONFIG.environmentVariables,
        buildSpec: this.getBuildSpec(),
        enableBranchAutoBuild: true,
        enableBranchAutoDeploy: true,
        enablePullRequestPreview: false // Disable to stay in free tier
      };

      const result = await this.amplify.createApp(params).promise();
      this.appId = result.app.appId;
      
      console.log(`‚úÖ Created Amplify app: ${result.app.name} (${this.appId})`);
    } catch (error) {
      throw new Error(`Failed to create Amplify app: ${error.message}`);
    }
  }

  getBuildSpec() {
    return `version: 1
applications:
  - frontend:
      phases:
        preBuild:
          commands:
            - echo "Installing dependencies for sandbox deployment"
            - cd frontend
            - npm ci --only=production
        build:
          commands:
            - echo "Building Next.js app for sandbox"
            - echo "Free tier mode: $NEXT_PUBLIC_FREE_TIER"
            - echo "AI disabled: $AI_ENABLED"
            - npm run build
      artifacts:
        baseDirectory: frontend/.next
        files:
          - '**/*'
      cache:
        paths:
          - frontend/node_modules/**/*
    appRoot: ./`;
  }

  async configureBuildSettings() {
    console.log('‚öôÔ∏è Configuring build settings...');
    
    const params = {
      appId: this.appId,
      buildSpec: this.getBuildSpec()
    };

    await this.amplify.updateApp(params).promise();
    console.log('‚úÖ Build settings configured');
  }

  async setEnvironmentVariables() {
    console.log('üîß Setting environment variables...');
    
    for (const [key, value] of Object.entries(CONFIG.environmentVariables)) {
      const params = {
        appId: this.appId,
        environmentName: 'main',
        environmentVariables: {
          [key]: value
        }
      };
      
      try {
        await this.amplify.putBackendEnvironment(params).promise();
      } catch (error) {
        // Environment might not exist yet, that's ok
        console.log(`Note: ${key} will be set during branch creation`);
      }
    }
    
    console.log('‚úÖ Environment variables configured');
  }

  async createBranch() {
    console.log('üåø Creating main branch...');
    
    try {
      const params = {
        appId: this.appId,
        branchName: CONFIG.branch,
        description: 'Main branch for sandbox deployment',
        enableAutoBuild: true,
        enablePullRequestPreview: false,
        environmentVariables: CONFIG.environmentVariables,
        tags: CONFIG.tags
      };

      const result = await this.amplify.createBranch(params).promise();
      console.log(`‚úÖ Created branch: ${result.branch.branchName}`);
    } catch (error) {
      if (error.code === 'DependentServiceFailureException' || error.message.includes('already exists')) {
        console.log('‚úÖ Branch already exists, updating configuration...');
        
        const updateParams = {
          appId: this.appId,
          branchName: CONFIG.branch,
          environmentVariables: CONFIG.environmentVariables,
          enableAutoBuild: true
        };
        
        await this.amplify.updateBranch(updateParams).promise();
        console.log('‚úÖ Branch configuration updated');
      } else {
        throw new Error(`Failed to create branch: ${error.message}`);
      }
    }
  }

  async startBuild() {
    console.log('üî® Starting build...');
    
    const params = {
      appId: this.appId,
      branchName: CONFIG.branch,
      jobType: 'RELEASE'
    };

    const result = await this.amplify.startJob(params).promise();
    const jobId = result.jobSummary.jobId;
    
    console.log(`‚úÖ Build started: ${jobId}`);
    console.log('‚è≥ Waiting for build to complete...');

    // Poll for build completion
    let attempts = 0;
    const maxAttempts = 60; // 30 minutes max
    
    while (attempts < maxAttempts) {
      await new Promise(resolve => setTimeout(resolve, 30000)); // Wait 30 seconds
      
      const statusParams = {
        appId: this.appId,
        branchName: CONFIG.branch,
        jobId: jobId
      };
      
      const status = await this.amplify.getJob(statusParams).promise();
      console.log(`üîÑ Build status: ${status.job.summary.status}`);
      
      if (status.job.summary.status === 'SUCCEED') {
        console.log('‚úÖ Build completed successfully!');
        return;
      } else if (status.job.summary.status === 'FAILED') {
        throw new Error('Build failed. Check Amplify console for details.');
      }
      
      attempts++;
    }
    
    throw new Error('Build timed out after 30 minutes');
  }

  async setupDomain() {
    console.log('üåê Setting up custom domain...');
    
    try {
      // Check if domain association already exists
      const existingDomains = await this.amplify.listDomainAssociations({
        appId: this.appId
      }).promise();
      
      const existingDomain = existingDomains.domainAssociations.find(
        domain => domain.domainName === CONFIG.domain
      );
      
      if (existingDomain) {
        console.log(`‚úÖ Using existing domain association: ${existingDomain.domainName}`);
        this.domainAssociationId = existingDomain.domainAssociationArn;
        return;
      }

      const params = {
        appId: this.appId,
        domainName: CONFIG.domain,
        enableAutoSubDomain: false,
        subDomainSettings: [
          {
            prefix: '',
            branchName: CONFIG.branch
          }
        ]
      };

      const result = await this.amplify.createDomainAssociation(params).promise();
      this.domainAssociationId = result.domainAssociation.domainAssociationArn;
      
      console.log(`‚úÖ Domain association created: ${CONFIG.domain}`);
      console.log('üìù Manual DNS setup required:');
      console.log('   Add the following CNAME record to your DNS:');
      console.log(`   Name: ${CONFIG.domain}`);
      console.log(`   Value: ${result.domainAssociation.certificateVerificationDNSRecord}`);
      
    } catch (error) {
      console.warn(`‚ö†Ô∏è Domain setup failed: ${error.message}`);
      console.log('üìù You can manually configure the domain in the Amplify console');
    }
  }

  async configureSSL() {
    console.log('üîí Configuring SSL certificate...');
    
    try {
      // List existing certificates
      const certs = await this.acm.listCertificates({
        CertificateStatuses: ['ISSUED', 'PENDING_VALIDATION']
      }).promise();
      
      // Look for existing certificate for the domain
      const existingCert = certs.CertificateSummaryList.find(
        cert => cert.DomainName === CONFIG.domain || 
                cert.SubjectAlternativeNameSummary?.includes(CONFIG.domain)
      );
      
      if (existingCert) {
        console.log(`‚úÖ Using existing SSL certificate: ${existingCert.CertificateArn}`);
        return existingCert.CertificateArn;
      }

      // Request new certificate
      const certParams = {
        DomainName: CONFIG.domain,
        ValidationMethod: 'DNS',
        Tags: Object.entries(CONFIG.tags).map(([Key, Value]) => ({ Key, Value }))
      };

      const certResult = await this.acm.requestCertificate(certParams).promise();
      console.log(`‚úÖ SSL certificate requested: ${certResult.CertificateArn}`);
      console.log('üìù Manual DNS validation required for SSL certificate');
      
      return certResult.CertificateArn;
      
    } catch (error) {
      console.warn(`‚ö†Ô∏è SSL configuration failed: ${error.message}`);
      console.log('üìù SSL certificate can be configured manually in ACM console');
    }
  }

  async verifyDeployment() {
    console.log('‚úÖ Verifying deployment...');
    
    const app = await this.amplify.getApp({ appId: this.appId }).promise();
    const branch = await this.amplify.getBranch({
      appId: this.appId,
      branchName: CONFIG.branch
    }).promise();

    console.log('\nüéâ Deployment Summary:');
    console.log(`üì± App Name: ${app.app.name}`);
    console.log(`üÜî App ID: ${this.appId}`);
    console.log(`üåø Branch: ${branch.branch.branchName}`);
    console.log(`üåê Default Domain: ${branch.branch.displayName}.${app.app.defaultDomain}`);
    console.log(`üéØ Custom Domain: ${CONFIG.domain} (if configured)`);
    console.log(`üìç Region: ${CONFIG.region}`);
    console.log(`üè∑Ô∏è Environment: FreeTier Sandbox`);
    
    console.log('\nüîó Useful Links:');
    console.log(`üìä Amplify Console: https://console.aws.amazon.com/amplify/home?region=${CONFIG.region}#/${this.appId}`);
    console.log(`üåê Live Site: https://${branch.branch.displayName}.${app.app.defaultDomain}`);
    
    this.saveSummary({
      appId: this.appId,
      appName: app.app.name,
      defaultDomain: `${branch.branch.displayName}.${app.app.defaultDomain}`,
      customDomain: CONFIG.domain,
      region: CONFIG.region,
      repository: CONFIG.repository,
      branch: CONFIG.branch
    });
  }

  async rollback() {
    console.log('üîÑ Starting rollback process...');
    
    try {
      if (this.domainAssociationId) {
        console.log('üóëÔ∏è Removing domain association...');
        await this.amplify.deleteDomainAssociation({
          appId: this.appId,
          domainName: CONFIG.domain
        }).promise();
      }
      
      if (this.appId) {
        console.log('üóëÔ∏è Deleting Amplify app...');
        await this.amplify.deleteApp({
          appId: this.appId
        }).promise();
      }
      
      console.log('‚úÖ Rollback completed');
    } catch (error) {
      console.error('‚ùå Rollback failed:', error.message);
      console.log('üìù Manual cleanup may be required in AWS console');
    }
  }

  saveSummary(summary) {
    const summaryPath = path.join(__dirname, '..', 'logs', 'amplify-sandbox-deployment.json');
    fs.writeFileSync(summaryPath, JSON.stringify(summary, null, 2));
    console.log(`üìÑ Deployment summary saved: ${summaryPath}`);
  }
}

// Rollback function for standalone use
async function rollbackDeployment() {
  console.log('üîÑ Starting standalone rollback...');
  
  const deployer = new AmplifyDeployer();
  
  try {
    // Load previous deployment info
    const summaryPath = path.join(__dirname, '..', 'logs', 'amplify-sandbox-deployment.json');
    if (fs.existsSync(summaryPath)) {
      const summary = JSON.parse(fs.readFileSync(summaryPath, 'utf8'));
      deployer.appId = summary.appId;
      deployer.domainAssociationId = summary.customDomain;
      
      await deployer.rollback();
      
      // Remove summary file
      fs.unlinkSync(summaryPath);
      console.log('‚úÖ Rollback completed and summary file removed');
    } else {
      console.log('‚ö†Ô∏è No deployment summary found. Manual cleanup may be required.');
    }
  } catch (error) {
    console.error('‚ùå Rollback failed:', error.message);
    process.exit(1);
  }
}

// Cost monitoring
async function checkFreeTierUsage() {
  console.log('üí∞ Checking free tier usage...');
  
  const costExplorer = new AWS.CostExplorer({ region: 'us-east-1' });
  
  const params = {
    TimePeriod: {
      Start: new Date(new Date().getFullYear(), new Date().getMonth(), 1).toISOString().split('T')[0],
      End: new Date().toISOString().split('T')[0]
    },
    Granularity: 'MONTHLY',
    Metrics: ['BlendedCost'],
    GroupBy: [{
      Type: 'DIMENSION',
      Key: 'SERVICE'
    }],
    Filter: {
      Dimensions: {
        Key: 'SERVICE',
        Values: ['AWS Amplify', 'Amazon CloudFront', 'Amazon Route 53']
      }
    }
  };
  
  try {
    const result = await costExplorer.getCostAndUsage(params).promise();
    console.log('üí∞ Current month costs:');
    
    result.ResultsByTime.forEach(period => {
      period.Groups.forEach(group => {
        const service = group.Keys[0];
        const cost = parseFloat(group.Metrics.BlendedCost.Amount);
        console.log(`   ${service}: $${cost.toFixed(2)}`);
      });
    });
  } catch (error) {
    console.warn('‚ö†Ô∏è Could not retrieve cost information:', error.message);
  }
}

// Main execution
async function main() {
  const args = process.argv.slice(2);
  
  if (args.includes('--rollback')) {
    await rollbackDeployment();
  } else if (args.includes('--cost-check')) {
    await checkFreeTierUsage();
  } else if (args.includes('--help')) {
    console.log('üìñ StackPro Amplify Sandbox Deployment');
    console.log('');
    console.log('Usage:');
    console.log('  node deploy-amplify-sandbox.js           # Deploy to sandbox');
    console.log('  node deploy-amplify-sandbox.js --rollback # Rollback deployment');
    console.log('  node deploy-amplify-sandbox.js --cost-check # Check free tier usage');
    console.log('  node deploy-amplify-sandbox.js --help    # Show this help');
  } else {
    const deployer = new AmplifyDeployer();
    await deployer.init();
    await checkFreeTierUsage();
  }
}

// Execute if run directly
if (require.main === module) {
  main().catch(error => {
    console.error('‚ùå Script failed:', error.message);
    process.exit(1);
  });
}

module.exports = { AmplifyDeployer, rollbackDeployment, checkFreeTierUsage };

#!/usr/bin/env node

/**
 * StackPro Free-Tier Mode Deployment
 * Creates a separate sandbox environment for demos without affecting production
 */

const { STSClient, GetCallerIdentityCommand } = require('@aws-sdk/client-sts');
const { BudgetsClient, CreateBudgetCommand } = require('@aws-sdk/client-budgets');
const { RDSClient, CreateDBInstanceCommand, DescribeDBInstancesCommand } = require('@aws-sdk/client-rds');
const { DynamoDBClient, CreateTableCommand, ListTablesCommand } = require('@aws-sdk/client-dynamodb');
const { S3Client, CreateBucketCommand, PutBucketLifecycleConfigurationCommand } = require('@aws-sdk/client-s3');
const { ApiGatewayV2Client, CreateApiCommand } = require('@aws-sdk/client-apigatewayv2');
const { LambdaClient, CreateFunctionCommand } = require('@aws-sdk/client-lambda');
const { EventBridgeClient, PutRuleCommand } = require('@aws-sdk/client-eventbridge');
const { SESv2Client, PutAccountSendingEnabledCommand } = require('@aws-sdk/client-sesv2');
const fs = require('fs').promises;
const path = require('path');
const { logger } = require('../src/utils/logger');

class FreeTierDeployer {
  constructor() {
    const region = 'us-west-2'; // Required by user specifications
    
    const clientConfig = {
      region,
      // Use default AWS credentials
    };

    // Initialize AWS clients
    this.sts = new STSClient(clientConfig);
    this.budgets = new BudgetsClient({ region: 'us-east-1' }); // Budgets API is only in us-east-1
    this.rds = new RDSClient(clientConfig);
    this.dynamodb = new DynamoDBClient(clientConfig);
    this.s3 = new S3Client(clientConfig);
    this.apigatewayv2 = new ApiGatewayV2Client(clientConfig);
    this.lambda = new LambdaClient(clientConfig);
    this.eventbridge = new EventBridgeClient(clientConfig);
    this.ses = new SESv2Client(clientConfig);
    
    this.region = region;
    
    // Free-tier configuration
    this.config = {
      environment: 'free-tier',
      stack: 'stackpro-sandbox',
      tags: {
        Project: 'StackPro',
        Env: 'FreeTier', 
        Owner: 'Ops',
        CostCenter: 'Sandbox'
      },
      limits: {
        maxDemoTenants: 3,
        fileTtlDays: 7,
        maxFileSize: '10MB',
        wsMaxMsgsPerMin: 60,
        apiRpm: 120
      }
    };

    // Resource plan (to be populated during dry run)
    this.resourcePlan = {
      rds: null,
      dynamodb: [],
      s3: null,
      apiGateway: null,
      lambda: [],
      budget: null,
      eventBridge: [],
      ses: null
    };

    this.estimatedCosts = {
      monthly: 0,
      breakdown: {}
    };
  }

  async getAccountId() {
    try {
      const command = new GetCallerIdentityCommand({});
      const result = await this.sts.send(command);
      return result.Account;
    } catch (error) {
      logger.error('Failed to get AWS account ID:', error);
      throw error;
    }
  }

  /**
   * Generate deployment plan without creating resources
   */
  async generateDeploymentPlan() {
    logger.info('üîç GENERATING FREE-TIER DEPLOYMENT PLAN');
    logger.info('=' .repeat(60));
    
    try {
      this.accountId = await this.getAccountId();
      logger.info(`AWS Account: ${this.accountId}`);
      logger.info(`Region: ${this.region}`);
      logger.info(`Environment: ${this.config.environment}`);
      logger.info('');

      // Generate plan for each service
      await this.planRDSInstance();
      await this.planDynamoDBTables();
      await this.planS3Bucket();
      await this.planWebSocketAPI();
      await this.planLambdaFunctions();
      await this.planCloudWatchMonitoring();
      await this.planBudgetAndAlerts();
      await this.planEventBridgeRules();
      await this.planSESConfiguration();

      // Calculate estimated costs
      this.calculateEstimatedCosts();

      // Export current configuration for rollback
      await this.exportCurrentConfiguration();

      // Display comprehensive deployment plan
      this.displayDeploymentPlan();
      
      return this.resourcePlan;

    } catch (error) {
      logger.error('‚ùå Failed to generate deployment plan:', error);
      throw error;
    }
  }

  async planRDSInstance() {
    const dbInstanceId = 'stackpro-sandbox-db';
    
    this.resourcePlan.rds = {
      identifier: dbInstanceId,
      instanceClass: 'db.t3.micro', // Free tier eligible
      engine: 'mysql',
      engineVersion: '8.0',
      allocatedStorage: 20, // Free tier: up to 20GB
      storageType: 'gp2',
      multiAZ: false, // Single AZ for free tier
      publiclyAccessible: false,
      vpcSecurityGroups: ['default'],
      dbSubnetGroup: 'default',
      backupRetentionPeriod: 7,
      storageEncrypted: true,
      tags: this.config.tags,
      estimated_cost_monthly: '$0 (Free Tier: 750 hours/month)'
    };
  }

  async planDynamoDBTables() {
    const tables = [
      {
        name: 'stackpro-sandbox-messages',
        partitionKey: 'tenantKey',
        sortKey: 'messageId',
        gsi: 'TimestampIndex',
        estimated_cost: '$0 (Free Tier: 25GB storage, 25 WCU/RCU)'
      },
      {
        name: 'stackpro-sandbox-connections', 
        partitionKey: 'tenantKey',
        sortKey: 'connectionId',
        ttl: 86400, // 24 hours
        estimated_cost: '$0 (Free Tier included)'
      },
      {
        name: 'stackpro-sandbox-rooms',
        partitionKey: 'tenantKey', 
        sortKey: 'roomId',
        estimated_cost: '$0 (Free Tier included)'
      }
    ];

    this.resourcePlan.dynamodb = tables.map(table => ({
      tableName: table.name,
      keySchema: [
        { AttributeName: table.partitionKey, KeyType: 'HASH' },
        { AttributeName: table.sortKey, KeyType: 'RANGE' }
      ],
      attributeDefinitions: [
        { AttributeName: table.partitionKey, AttributeType: 'S' },
        { AttributeName: table.sortKey, AttributeType: 'S' }
      ],
      billingMode: 'PAY_PER_REQUEST',
      timeToLiveSpecification: table.ttl ? {
        AttributeName: 'ttl',
        Enabled: true
      } : undefined,
      tags: this.config.tags,
      estimated_cost_monthly: table.estimated_cost
    }));
  }

  async planS3Bucket() {
    this.resourcePlan.s3 = {
      bucketName: 'stackpro-sandbox-assets',
      region: this.region,
      lifecycleRules: [
        {
          id: 'DeleteDemoFiles',
          status: 'Enabled',
          filter: { prefix: 'demo/' },
          expiration: { days: 7 }
        }
      ],
      versioning: 'Suspended',
      publicAccessBlock: {
        blockPublicAcls: true,
        blockPublicPolicy: true,
        ignorePublicAcls: true,
        restrictPublicBuckets: true
      },
      tags: this.config.tags,
      estimated_cost_monthly: '$0 (Free Tier: 5GB storage, 20k GET, 2k PUT)'
    };
  }

  async planWebSocketAPI() {
    // Check if we can reuse existing WebSocket API
    const existingApiId = 'c7zc4l0r88'; // From previous deployment
    
    this.resourcePlan.apiGateway = {
      reuseExisting: true,
      existingApiId: existingApiId,
      existingEndpoint: `wss://${existingApiId}.execute-api.${this.region}.amazonaws.com/prod`,
      newSandboxStage: 'sandbox',
      newEndpoint: `wss://${existingApiId}.execute-api.${this.region}.amazonaws.com/sandbox`,
      routes: ['$connect', '$disconnect', '$default', 'sendMessage', 'joinRoom'],
      estimated_cost_monthly: '$0 (Free Tier: 1M messages/month)'
    };
  }

  async planLambdaFunctions() {
    const functions = [
      {
        name: 'stackpro-sandbox-ws-connect',
        handler: 'websocket-connect.handler',
        runtime: 'nodejs18.x',
        memorySize: 128, // Minimum for cost optimization
        timeout: 15,
        description: 'WebSocket connection handler for sandbox'
      },
      {
        name: 'stackpro-sandbox-ws-disconnect',
        handler: 'websocket-disconnect.handler', 
        runtime: 'nodejs18.x',
        memorySize: 128,
        timeout: 15,
        description: 'WebSocket disconnection handler for sandbox'
      },
      {
        name: 'stackpro-sandbox-message-router',
        handler: 'message-router.handler',
        runtime: 'nodejs18.x', 
        memorySize: 256,
        timeout: 30,
        description: 'Message routing and processing for sandbox'
      }
    ];

    this.resourcePlan.lambda = functions.map(func => ({
      ...func,
      environment: {
        ENV: 'free-tier',
        AI_ENABLED: 'false',
        MAX_DEMO_TENANTS: '3',
        FILE_TTL_DAYS: '7',
        WS_MAX_MSGS_PER_MIN: '60',
        API_RPM: '120'
      },
      tags: this.config.tags,
      estimated_cost_monthly: '$0 (Free Tier: 1M requests, 400k GB-seconds)'
    }));
  }

  async planCloudWatchMonitoring() {
    this.resourcePlan.cloudWatch = {
