router.get('/stats', async (req, res) => {
  const clientId = getClientId(req);
  const userId = getUserId(req);

  try {
    // Get basic stats
    const channels = await messagingService.getClientChannels(clientId, userId);
    const presence = await messagingService.getClientPresence(clientId);
    const notifications = await messagingService.getUserNotifications(userId, 10);

    // Calculate additional stats
    const totalChannels = channels.length;
    const publicChannels = channels.filter(ch => ch.channelType === 'public').length;
    const privateChannels = channels.filter(ch => ch.channelType === 'private').length;
    const documentChannels = channels.filter(ch => ch.channelType === 'document').length;
    
    const onlineUsers = Object.values(presence).filter(p => p.status === 'online').length;
    const totalUsers = Object.keys(presence).length;
    
    const unreadNotifications = notifications.filter(n => n.status === 'unread').length;

    const stats = {
      channels: {
        total: totalChannels,
        public: publicChannels,
        private: privateChannels,
        document: documentChannels
      },
      users: {
        total: totalUsers,
        online: onlineUsers,
        offline: totalUsers - onlineUsers
      },
      notifications: {
        total: notifications.length,
        unread: unreadNotifications
      },
      activity: {
        activeChannels: channels.filter(ch => ch.lastActivity).length,
        recentChannels: channels.slice(0, 5).map(ch => ({
          id: ch.channelId,
          name: ch.name,
          lastActivity: ch.lastActivity
        }))
      }
    };

    res.json({
      success: true,
      stats
    });

  } catch (error) {
    logger.error('Failed to get messaging stats', {
      clientId,
      userId,
      error: error.message
    });

    res.status(500).json({
      success: false,
      error: error.message
    });
  }
});

/**
 * @route GET /api/messaging/health
 * @desc Health check for messaging services
 * @access Private
 */
router.get('/health', async (req, res) => {
  const clientId = getClientId(req);
  const health = {
    status: 'healthy',
    timestamp: new Date().toISOString(),
    services: {}
  };

  try {
    // Test messaging service
    try {
      await messagingService.getClientPresence(clientId);
      health.services.messagingService = 'healthy';
    } catch (error) {
      health.services.messagingService = 'unhealthy';
      health.status = 'degraded';
    }

    // Test database connectivity
    try {
      await messagingService.dynamodb.scan({
        TableName: 'StackPro-Channels',
        Limit: 1
      }).promise();
      health.services.database = 'healthy';
    } catch (error) {
      health.services.database = 'unhealthy';
      health.status = 'degraded';
    }

    const statusCode = health.status === 'healthy' ? 200 : 503;
    res.status(statusCode).json({
      success: health.status !== 'unhealthy',
      health
    });

  } catch (error) {
    logger.error('Messaging health check failed', {
      clientId,
      error: error.message
    });

    res.status(500).json({
      success: false,
      health: {
        status: 'unhealthy',
        error: error.message,
        timestamp: new Date().toISOString()
      }
    });
  }
});

// Error handling middleware specific to messaging routes
router.use((error, req, res, next) => {
  logger.error('Messaging API Error', {
    error: error.message,
    stack: error.stack,
    path: req.path,
    method: req.method,
    clientId: getClientId(req),
    userId: getUserId(req)
  });

  // Generic error response
  res.status(500).json({
    success: false,
    error: 'An unexpected error occurred. Please try again.'
  });
});

module.exports = router;

const AWS = require('aws-sdk');
const { logger } = require('../logger');

class NotificationService {
  constructor() {
    this.sns = new AWS.SNS();
    this.sqs = new AWS.SQS();
    this.ses = new AWS.SES();
    this.dynamodb = new AWS.DynamoDB.DocumentClient();
    
    // Service configuration
    this.region = process.env.AWS_REGION || 'us-west-2';
    this.accountId = process.env.AWS_ACCOUNT_ID;
    this.notificationsTable = 'StackPro-Notifications';
  }

  /**
   * Setup notification infrastructure for a client
   */
  async setupClientNotifications(clientId) {
    try {
      logger.info('Setting up notification infrastructure', { clientId });

      // Create SNS topic for client notifications
      const topicResult = await this.createNotificationTopic(clientId);
      
      // Create SQS queue for message processing
      const queueResult = await this.createMessageQueue(clientId);
      
      // Subscribe SQS to SNS for fanout
      await this.subscribeQueueToTopic(topicResult.TopicArn, queueResult.QueueUrl);
      
      // Setup dead letter queue for failed notifications
      const dlqResult = await this.createDeadLetterQueue(clientId);
      
      logger.info('Notification infrastructure setup complete', {
        clientId,
        topicArn: topicResult.TopicArn,
        queueUrl: queueResult.QueueUrl,
        dlqUrl: dlqResult.QueueUrl
      });

      return {
        topicArn: topicResult.TopicArn,
        queueUrl: queueResult.QueueUrl,
        dlqUrl: dlqResult.QueueUrl
      };

    } catch (error) {
      logger.error('Failed to setup notification infrastructure', {
        clientId,
        error: error.message
      });
      throw error;
    }
  }

  /**
   * Send multi-channel notification
   */
  async sendNotification(userId, clientId, notification) {
    try {
      const notificationId = this.generateNotificationId();
      const timestamp = new Date().toISOString();

      // Store notification in database
      await this.storeNotification({
        userId,
        clientId,
        notificationId,
        ...notification,
        timestamp
      });

      // Get user notification preferences
      const preferences = await this.getUserNotificationPreferences(userId, clientId);

      // Send via enabled channels
      const results = await Promise.allSettled([
        // Email notification
        preferences.email && this.sendEmailNotification(userId, clientId, notification),
        
        // Push notification (via SNS)
        preferences.push && this.sendPushNotification(userId, clientId, notification),
        
        // SMS notification
        preferences.sms && this.sendSMSNotification(userId, clientId, notification),
        
        // In-app notification (via WebSocket)
        preferences.inApp && this.sendInAppNotification(userId, clientId, notification)
      ].filter(Boolean));

      // Log results
      const successful = results.filter(r => r.status === 'fulfilled').length;
      const failed = results.filter(r => r.status === 'rejected').length;

      logger.info('Multi-channel notification sent', {
        notificationId,
        userId,
        clientId,
        channels: Object.keys(preferences).filter(k => preferences[k]),
        successful,
        failed
      });

      return {
        notificationId,
        successful,
        failed,
        results
      };

    } catch (error) {
      logger.error('Failed to send notification', {
        userId,
        clientId,
        error: error.message
      });
      throw error;
    }
  }

  /**
   * Send email notification via SES
   */
  async sendEmailNotification(userId, clientId, notification) {
    try {
      // Get user email
      const userEmail = await this.getUserEmail(userId, clientId);
      if (!userEmail) {
        throw new Error('User email not found');
      }

      const emailParams = {
        Source: `StackPro <noreply@stackpro.io>`,
        Destination: {
          ToAddresses: [userEmail]
        },
        Message: {
          Subject: {
            Data: notification.title,
            Charset: 'UTF-8'
          },
          Body: {
            Html: {
              Data: this.generateEmailHTML(notification),
              Charset: 'UTF-8'
            },
            Text: {
              Data: notification.body,
              Charset: 'UTF-8'
            }
          }
        },
        Tags: [
          {
            Name: 'ClientId',
            Value: clientId
          },
          {
            Name: 'NotificationType',
            Value: notification.type
          }
        ]
      };

      const result = await this.ses.sendEmail(emailParams).promise();
      
      logger.info('Email notification sent', {
        userId,
        clientId,
        messageId: result.MessageId,
        type: notification.type
      });

      return result;

    } catch (error) {
      logger.error('Failed to send email notification', {
        userId,
        clientId,
        error: error.message
      });
      throw error;
    }
  }

  /**
   * Send push notification via SNS
   */
  async sendPushNotification(userId, clientId, notification) {
    try {
      // Get user push endpoint
      const pushEndpoint = await this.getUserPushEndpoint(userId, clientId);
      if (!pushEndpoint) {
        throw new Error('User push endpoint not found');
      }

      const message = {
        default: notification.body,
        GCM: JSON.stringify({
          data: {
            title: notification.title,
            body: notification.body,
            type: notification.type,
            ...notification.data
          }
        }),
        APNS: JSON.stringify({
          aps: {
            alert: {
              title: notification.title,
              body: notification.body
            },
            badge: 1,
            sound: 'default'
          },
          type: notification.type,
          ...notification.data
        })
      };

      const params = {
        TargetArn: pushEndpoint,
        Message: JSON.stringify(message),
        MessageStructure: 'json'
      };

      const result = await this.sns.publish(params).promise();
      
      logger.info('Push notification sent', {
        userId,
        clientId,
        messageId: result.MessageId,
        type: notification.type
      });

      return result;

    } catch (error) {
      logger.error('Failed to send push notification', {
        userId,
        clientId,
        error: error.message
      });
      throw error;
    }
  }

  /**
   * Send SMS notification via SNS
   */
  async sendSMSNotification(userId, clientId, notification) {
    try {
      // Get user phone number
      const phoneNumber = await this.getUserPhoneNumber(userId, clientId);
      if (!phoneNumber) {
        throw new Error('User phone number not found');
      }

      const message = `${notification.title}\n\n${notification.body}`;

      const params = {
        PhoneNumber: phoneNumber,
        Message: message,
        MessageAttributes: {
          'AWS.SNS.SMS.SMSType': {
            DataType: 'String',
            StringValue: 'Transactional'
          },
          'ClientId': {
            DataType: 'String',
            StringValue: clientId
          },
          'NotificationType': {
            DataType: 'String',
            StringValue: notification.type
          }
        }
      };

      const result = await this.sns.publish(params).promise();
      
      logger.info('SMS notification sent', {
        userId,
        clientId,
        messageId: result.MessageId,
        type: notification.type
      });

      return result;

    } catch (error) {
      logger.error('Failed to send SMS notification', {
        userId,
        clientId,
        error: error.message
      });
      throw error;
    }
  }

  /**
   * Send in-app notification via WebSocket
   */
  async sendInAppNotification(userId, clientId, notification) {
    try {
      // This would integrate with the WebSocket handler
      // For now, we'll publish to the client's notification topic
      const topicArn = `arn:aws:sns:${this.region}:${this.accountId}:stackpro-notifications-${clientId}`;

      const message = {
        type: 'in-app-notification',
        userId,
        notification
      };

      const params = {
        TopicArn: topicArn,
        Message: JSON.stringify(message),
        Subject: 'In-App Notification',
        MessageAttributes: {
          'NotificationType': {
            DataType: 'String',
            StringValue: 'in-app'
          },
          'TargetUserId': {
            DataType: 'String',
            StringValue: userId
          }
        }
      };

      const result = await this.sns.publish(params).promise();
      
      logger.info('In-app notification sent', {
        userId,
        clientId,
        messageId: result.MessageId,
        type: notification.type
      });

      return result;

    } catch (error) {
      logger.error('Failed to send in-app notification', {
        userId,
        clientId,
        error: error.message
      });
      throw error;
    }
  }

  /**
   * Process notification queue messages
   */
  async processNotificationQueue(queueUrl) {
    try {
      const params = {
        QueueUrl: queueUrl,
        MaxNumberOfMessages: 10,
        WaitTimeSeconds: 20,
        MessageAttributeNames: ['All']
      };

      const result = await this.sqs.receiveMessage(params).promise();
      const messages = result.Messages || [];

      logger.info('Processing notification queue', {
        queueUrl,
        messageCount: messages.length
      });

      for (const message of messages) {
        try {
          await this.processNotificationMessage(message);
          
          // Delete processed message
          await this.sqs.deleteMessage({
            QueueUrl: queueUrl,
            ReceiptHandle: message.ReceiptHandle
          }).promise();

        } catch (error) {
          logger.error('Failed to process notification message', {
            messageId: message.MessageId,
            error: error.message
          });
        }
      }

      return messages.length;

    } catch (error) {
      logger.error('Failed to process notification queue', {
        queueUrl,
        error: error.message
      });
      throw error;
    }
  }

  /**
   * Get user notification preferences
   */
  async getUserNotificationPreferences(userId, clientId) {
    try {
      // Default preferences - in production, this would come from database
      return {
        email: true,
        push: true,
        sms: false,
        inApp: true
      };
    } catch (error) {
      // Return defaults if can't get preferences
      return {
        email: true,
        push: false,
        sms: false,
        inApp: true
      };
    }
  }

  // Helper methods

  async createNotificationTopic(clientId) {
    const topicName = `stackpro-notifications-${clientId}`;
    
    const params = {
      Name: topicName,
      Tags: [
        {
          Key: 'ClientId',
          Value: clientId
        },
        {
          Key: 'Service',
          Value: 'StackPro-Messaging'
        }
      ]
    };

    return await this.sns.createTopic(params).promise();
  }

  async createMessageQueue(clientId) {
    const queueName = `stackpro-messaging-${clientId}`;
    
    const params = {
      QueueName: queueName,
      Attributes: {
        'MessageRetentionPeriod': '1209600', // 14 days
        'VisibilityTimeoutSeconds': '60',
        'MaxReceiveCount': '3'
      },
      tags: {
        'ClientId': clientId,
        'Service': 'StackPro-Messaging'
      }
    };

    return await this.sqs.createQueue(params).promise();
  }

  async createDeadLetterQueue(clientId) {
    const queueName = `stackpro-messaging-dlq-${clientId}`;
    
    const params = {
      QueueName: queueName,
      Attributes: {
        'MessageRetentionPeriod': '1209600' // 14 days
      },
      tags: {
        'ClientId': clientId,
        'Service': 'StackPro-Messaging',
        'Type': 'DeadLetter'
      }
    };

    return await this.sqs.createQueue(params).promise();
  }

  async subscribeQueueToTopic(topicArn, queueUrl) {
    const params = {
      Protocol: 'sqs',
      TopicArn: topicArn,
      Endpoint: queueUrl
    };

    return await this.sns.subscribe(params).promise();
  }

  async storeNotification(notification) {
    const ttl = Math.floor(Date.now() / 1000) + (30 * 24 * 60 * 60); // 30 days

    const item = {
      ...notification,
      status: 'sent',
      ttl
    };

    await this.dynamodb.put({
      TableName: this.notificationsTable,
      Item: item
    }).promise();
  }

  async getUserEmail(userId, clientId) {
    // In production, this would query the user database
    // For now, return a placeholder
    return `${userId}@demo.com`;
  }

  async getUserPushEndpoint(userId, clientId) {
    // In production, this would query stored push endpoints
    return null;
  }

  async getUserPhoneNumber(userId, clientId) {
    // In production, this would query the user database
    return null;
  }

  generateEmailHTML(notification) {
    return `
      <!DOCTYPE html>
      <html>
      <head>
        <meta charset="utf-8">
        <title>${notification.title}</title>
        <style>
          body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
          .container { max-width: 600px; margin: 0 auto; padding: 20px; }
          .header { background: #007bff; color: white; padding: 20px; text-align: center; }
          .content { padding: 20px; background: #f8f9fa; }
          .footer { padding: 10px; text-align: center; font-size: 12px; color: #666; }
        </style>
      </head>
      <body>
        <div class="container">
          <div class="header">
            <h1>StackPro</h1>
          </div>
          <div class="content">
            <h2>${notification.title}</h2>
            <p>${notification.body}</p>
          </div>
          <div class="footer">
            <p>This is an automated message from StackPro. Please do not reply.</p>
          </div>
        </div>
      </body>
      </html>
    `;
  }

  async processNotificationMessage(message) {
    const body = JSON.parse(message.Body);
    
    // Handle different message types
    switch (body.type) {
      case 'in-app-notification':
        // Forward to WebSocket connections
        await this.forwardToWebSocket(body);
        break;
      
      case 'email-notification':
        // Handle email-specific processing
        await this.processEmailNotification(body);
        break;
      
      default:
        logger.warn('Unknown notification message type', {
          type: body.type,
          messageId: message.MessageId
        });
    }
  }

  async forwardToWebSocket(message) {
    // This would integrate with the WebSocket handler to send real-time notifications
    logger.info('Forwarding to WebSocket', {
      userId: message.userId,
      type: message.notification.type
    });
  }

  async processEmailNotification(message) {
    // Handle email-specific processing
    logger.info('Processing email notification', {
      userId: message.userId,
      type: message.notification.type
    });
  }

  generateNotificationId() {
    return `notif-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
  }
}

module.exports = NotificationService;

const AWS = require('aws-sdk');
const { logger } = require('../logger');

// Initialize AWS services
const dynamodb = new AWS.DynamoDB.DocumentClient();
const apigateway = new AWS.ApiGatewayManagementApi({
  endpoint: process.env.WEBSOCKET_API_ENDPOINT
});

class WebSocketHandler {
  constructor() {
    this.connectionsTable = 'StackPro-Connections';
    this.messagesTable = 'StackPro-Messages';
    this.channelsTable = 'StackPro-Channels';
    this.presenceTable = 'StackPro-Presence';
  }

  /**
   * Handle WebSocket connection
   */
  async handleConnect(event) {
    const { connectionId } = event.requestContext;
    const { userId, clientId } = this.extractUserInfo(event);

    logger.info('WebSocket connection request', {
      connectionId,
      userId,
      clientId
    });

    try {
      // Store connection information
      await this.storeConnection(connectionId, userId, clientId);

      // Update user presence
      await this.updatePresence(userId, clientId, 'online', connectionId);

      // Broadcast presence update to client users
      await this.broadcastPresenceUpdate(clientId, userId, 'online');

      logger.info('WebSocket connection established', {
        connectionId,
        userId,
        clientId
      });

      return {
        statusCode: 200,
        body: JSON.stringify({ message: 'Connected successfully' })
      };

    } catch (error) {
      logger.error('WebSocket connection failed', {
        connectionId,
        userId,
        clientId,
        error: error.message
      });

      return {
        statusCode: 500,
        body: JSON.stringify({ 
          error: 'Connection failed',
          message: error.message 
        })
      };
    }
  }

  /**
   * Handle WebSocket disconnection
   */
  async handleDisconnect(event) {
    const { connectionId } = event.requestContext;

    logger.info('WebSocket disconnection request', { connectionId });

    try {
      // Get connection info before deletion
      const connection = await this.getConnection(connectionId);
      
      if (connection) {
        const { userId, clientId } = connection;

        // Remove connection
        await this.removeConnection(connectionId);

        // Check if user has other active connections
        const activeConnections = await this.getUserConnections(userId);
        const status = activeConnections.length > 0 ? 'online' : 'offline';

        // Update presence
        await this.updatePresence(userId, clientId, status);

        // Broadcast presence update if user went offline
        if (status === 'offline') {
          await this.broadcastPresenceUpdate(clientId, userId, 'offline');
        }

        logger.info('WebSocket disconnection processed', {
          connectionId,
          userId,
          clientId,
          status
        });
      }

      return {
        statusCode: 200,
        body: JSON.stringify({ message: 'Disconnected successfully' })
      };

    } catch (error) {
      logger.error('WebSocket disconnection failed', {
        connectionId,
        error: error.message
      });

      return {
        statusCode: 500,
        body: JSON.stringify({ 
          error: 'Disconnection failed',
          message: error.message 
        })
      };
    }
  }

  /**
   * Handle WebSocket messages
   */
  async handleMessage(event) {
    const { connectionId } = event.requestContext;
    const body = JSON.parse(event.body || '{}');

    logger.info('WebSocket message received', {
      connectionId,
      action: body.action,
      messageType: body.type
    });

    try {
      // Get connection info
      const connection = await this.getConnection(connectionId);
      if (!connection) {
        throw new Error('Connection not found');
      }

      const { userId, clientId } = connection;

      // Route message based on action
      switch (body.action) {
        case 'sendMessage':
          return await this.handleSendMessage(body, userId, clientId, connectionId);
        
        case 'joinChannel':
          return await this.handleJoinChannel(body, userId, clientId, connectionId);
        
        case 'leaveChannel':
          return await this.handleLeaveChannel(body, userId, clientId, connectionId);
        
        case 'typing':
          return await this.handleTyping(body, userId, clientId, connectionId);
        
        case 'markAsRead':
          return await this.handleMarkAsRead(body, userId, clientId, connectionId);
        
        case 'getMessages':
          return await this.handleGetMessages(body, userId, clientId, connectionId);
        
        case 'heartbeat':
          return await this.handleHeartbeat(userId, clientId, connectionId);
        
        default:
          throw new Error(`Unknown action: ${body.action}`);
      }

    } catch (error) {
      logger.error('WebSocket message handling failed', {
        connectionId,
        action: body.action,
        error: error.message
      });

      // Send error back to client
      await this.sendToConnection(connectionId, {
        type: 'error',
        error: error.message,
        originalAction: body.action
      });

      return {
        statusCode: 500,
        body: JSON.stringify({ 
          error: 'Message handling failed',
          message: error.message 
        })
      };
    }
  }

  /**
   * Handle sending a message
   */
  async handleSendMessage(body, userId, clientId, connectionId) {
    const { channelId, content, messageType = 'text', threadId, attachments } = body;

    // Validate input
    if (!channelId || !content) {
      throw new Error('channelId and content are required');
    }

    // Generate message ID
    const messageId = this.generateMessageId();
    const timestamp = new Date().toISOString();

    // Create message object
    const message = {
      messageId,
      channelId,
      clientId,
      userId,
      content,
      messageType,
      timestamp,
      threadId: threadId || null,
      attachments: attachments || [],
      reactions: {},
      edited: false,
      deleted: false
    };

    // Store message in DynamoDB
    await dynamodb.put({
      TableName: this.messagesTable,
      Item: message
    }).promise();

    // Broadcast message to channel subscribers
    await this.broadcastToChannel(channelId, {
      type: 'message',
      message
    });

    // Send confirmation back to sender
    await this.sendToConnection(connectionId, {
      type: 'messageConfirmation',
      messageId,
      timestamp
    });

    logger.info('Message sent successfully', {
      messageId,
      channelId,
      userId,
      clientId
    });

    return {
      statusCode: 200,
      body: JSON.stringify({ 
        success: true,
        messageId,
        timestamp 
      })
    };
  }

  /**
   * Handle joining a channel
   */
  async handleJoinChannel(body, userId, clientId, connectionId) {
    const { channelId } = body;

    if (!channelId) {
      throw new Error('channelId is required');
    }

    // Verify channel exists and user has access
    const channel = await this.getChannel(channelId);
    if (!channel || channel.clientId !== clientId) {
      throw new Error('Channel not found or access denied');
    }

    // Add user to channel subscribers (stored in connection record)
    await this.addChannelSubscription(connectionId, channelId);

    // Send channel info and recent messages
    const recentMessages = await this.getRecentMessages(channelId, 50);
    
    await this.sendToConnection(connectionId, {
      type: 'channelJoined',
      channelId,
      channel,
      messages: recentMessages
    });

    logger.info('User joined channel', {
      userId,
      clientId,
      channelId
    });

    return {
      statusCode: 200,
      body: JSON.stringify({ 
        success: true,
        channelId 
      })
    };
  }

  /**
   * Handle typing indicators
   */
  async handleTyping(body, userId, clientId, connectionId) {
    const { channelId, isTyping } = body;

    if (!channelId) {
      throw new Error('channelId is required');
    }

    // Broadcast typing indicator to channel (except sender)
    await this.broadcastToChannel(channelId, {
      type: 'typing',
      channelId,
      userId,
      isTyping: Boolean(isTyping)
    }, connectionId); // Exclude sender

    return {
      statusCode: 200,
      body: JSON.stringify({ success: true })
    };
  }

  /**
   * Handle heartbeat to maintain connection
   */
  async handleHeartbeat(userId, clientId, connectionId) {
    // Update last seen timestamp
    await this.updatePresence(userId, clientId, 'online', connectionId);

    return {
      statusCode: 200,
      body: JSON.stringify({ 
        success: true,
        timestamp: new Date().toISOString()
      })
    };
  }

  /**
   * Store WebSocket connection
   */
  async storeConnection(connectionId, userId, clientId) {
    const ttl = Math.floor(Date.now() / 1000) + (24 * 60 * 60); // 24 hours

    await dynamodb.put({
      TableName: this.connectionsTable,
      Item: {
        connectionId,
        userId,
        clientId,
        connectedAt: new Date().toISOString(),
        channels: [], // Subscribed channels
        ttl
      }
    }).promise();
  }

  /**
   * Get connection information
   */
  async getConnection(connectionId) {
    const result = await dynamodb.get({
      TableName: this.connectionsTable,
      Key: { connectionId }
    }).promise();

    return result.Item || null;
  }

  /**
   * Remove connection
   */
  async removeConnection(connectionId) {
    await dynamodb.delete({
      TableName: this.connectionsTable,
      Key: { connectionId }
    }).promise();
  }

  /**
   * Get user's active connections
   */
  async getUserConnections(userId) {
    const result = await dynamodb.query({
      TableName: this.connectionsTable,
      IndexName: 'UserIndex',
      KeyConditionExpression: 'userId = :userId',
      ExpressionAttributeValues: {
        ':userId': userId
      }
    }).promise();

    return result.Items || [];
  }

  /**
   * Update user presence
   */
  async updatePresence(userId, clientId, status, connectionId = null) {
    const now = new Date().toISOString();
    const ttl = Math.floor(Date.now() / 1000) + (7 * 24 * 60 * 60); // 7 days

    const item = {
      userId,
      clientId,
      status,
      lastSeen: now,
      connectionId,
      ttl
    };

    // Add additional status info based on status
    if (status === 'online') {
      item.connectedAt = now;
    }

    await dynamodb.put({
      TableName: this.presenceTable,
      Item: item
    }).promise();
  }

  /**
   * Broadcast presence update to client users
   */
  async broadcastPresenceUpdate(clientId, userId, status) {
    // Get all connections for the client
    const connections = await this.getClientConnections(clientId);

    const presenceUpdate = {
      type: 'presenceUpdate',
      userId,
      status,
      timestamp: new Date().toISOString()
    };

    // Send to all client connections
    await Promise.all(
      connections.map(conn => 
        this.sendToConnection(conn.connectionId, presenceUpdate)
          .catch(error => 
            logger.warn('Failed to send presence update', {
              connectionId: conn.connectionId,
              error: error.message
            })
          )
      )
    );
  }

  /**
   * Broadcast message to channel subscribers
   */
  async broadcastToChannel(channelId, message, excludeConnectionId = null) {
    // Get all connections subscribed to this channel
    const connections = await this.getChannelConnections(channelId);

    await Promise.all(
      connections
        .filter(conn => conn.connectionId !== excludeConnectionId)
        .map(conn => 
          this.sendToConnection(conn.connectionId, message)
            .catch(error => {
              logger.warn('Failed to broadcast to connection', {
                connectionId: conn.connectionId,
                channelId,
                error: error.message
              });
              // Clean up dead connections
              return this.removeConnection(conn.connectionId);
            })
        )
    );
  }

  /**
   * Get connections for a channel
   */
  async getChannelConnections(channelId) {
    // This is a simplified version - in production, you'd maintain 
    // channel subscription mappings more efficiently
    const result = await dynamodb.scan({
      TableName: this.connectionsTable,
      FilterExpression: 'contains(channels, :channelId)',
      ExpressionAttributeValues: {
        ':channelId': channelId
      }
    }).promise();

    return result.Items || [];
  }

  /**
   * Get all connections for a client
   */
  async getClientConnections(clientId) {
    const result = await dynamodb.query({
      TableName: this.connectionsTable,
      IndexName: 'ClientIndex',
      KeyConditionExpression: 'clientId = :clientId',
      ExpressionAttributeValues: {
        ':clientId': clientId
      }
    }).promise();

    return result.Items || [];
  }

  /**
   * Send message to specific connection
   */
  async sendToConnection(connectionId, message) {
    try {
      await apigateway.postToConnection({
        ConnectionId: connectionId,
        Data: JSON.stringify(message)
      }).promise();

    } catch (error) {
      if (error.statusCode === 410) {
        // Connection is gone, remove it
        await this.removeConnection(connectionId);
      }
      throw error;
    }
  }

  /**
   * Add channel subscription to connection
   */
  async addChannelSubscription(connectionId, channelId) {
    await dynamodb.update({
      TableName: this.connectionsTable,
      Key: { connectionId },
      UpdateExpression: 'ADD channels :channelSet',
      ExpressionAttributeValues: {
        ':channelSet': dynamodb.createSet([channelId])
      }
    }).promise();
  }

  /**
   * Get channel information
   */
  async getChannel(channelId) {
    const result = await dynamodb.get({
      TableName: this.channelsTable,
      Key: { channelId }
    }).promise();

    return result.Item || null;
  }

  /**
   * Get recent messages for a channel
   */
  async getRecentMessages(channelId, limit = 50) {
    const result = await dynamodb.query({
      TableName: this.messagesTable,
      KeyConditionExpression: 'channelId = :channelId',
      ExpressionAttributeValues: {
        ':channelId': channelId
      },
      ScanIndexForward: false, // Most recent first
      Limit: limit
    }).promise();

    return (result.Items || []).reverse(); // Return in chronological order
  }

  /**
   * Extract user info from WebSocket event
   */
  extractUserInfo(event) {
    // Extract from query string parameters or authorization header
    const queryParams = event.queryStringParameters || {};
    const headers = event.headers || {};

    // In production, you'd validate JWT token here
    const token = queryParams.token || headers.Authorization;
    
    // For now, extract from query params (replace with JWT validation)
    return {
      userId: queryParams.userId || 'anonymous',
      clientId: queryParams.clientId || 'demo-client'
    };
  }

  /**
   * Generate unique message ID
   */
  generateMessageId() {
    return `msg-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
  }
}

// Lambda handler functions
const webSocketHandler = new WebSocketHandler();

/**
 * Lambda handler for WebSocket $connect route
 */
exports.connectHandler = async (event, context) => {
  return await webSocketHandler.handleConnect(event);
};

/**
 * Lambda handler for WebSocket $disconnect route
 */
exports.disconnectHandler = async (event, context) => {
  return await webSocketHandler.handleDisconnect(event);
};

/**
 * Lambda handler for WebSocket message routing
 */
exports.messageHandler = async (event, context) => {
  return await webSocketHandler.handleMessage(event);
};

module.exports = WebSocketHandler;
